import React, {useState} from "react";



export default function TextForm(props){
    const handleUpClick = ()=>{
        console.log("Uppercase was clicked" + text);
        let newText = text.toUpperCase();
        setText(newText);
    }

    const handleOnChange = (event)=>{
        console.log("On change");
        setText(event.target.value);
    }

    const [text, setText] = useState('Enter text here...');

    return(
        <>
        <div className="container">
            <h1>{props.heading} </h1>
<div className="mb-3">
  <label htmlFor="exampleFormControlTextarea1" className="form-label"></label>
  <textarea className="form-control" onChange={handleOnChange} value={text} id="exampleFormControlTextarea1" rows="8"></textarea>
</div>
<button className="btn btn-primary" onClick={handleUpClick}>Convort to uppercase </button>
        </div>
        <div className="container my-2">
            <h1>Your text summary</h1>
            <p>{text.split (" ") .length} word and {text.length} charactrs</p>
            <p>{0.008 * text.split(" ").length} Minutes Read</p>
        </div>

</>
    )
}
